import { useState } from "react";
import './App.css';
import Header from './components/Header';
import Game from './components/Game';
import Footer from './components/Footer';


function App() {
  const [score, setScore] = useState({
    score: 0,
    maxScore: 0,
  });

  const scorePoint = () => {
    setScore(score + 1);
  }

  const resetScore = () => {
    setScore(0);
  }

  return (
    <div className="App">
      <Header score={score}/>
      <Game />
      <Footer />
    </div>
  );
}

export default App;
