//Game.js
import React, { Component } from "react";
import { useState, useEffect } from "react";
import uniqid from 'uniqid';
import SingleCard from "./singlecard";

const cardImages = [
  {"src": "images/charmander.png" },
  {"src": "images/cloyster.png" },
  {"src": "images/kingler.png" },
  {"src": "images/machop.png" },
  {"src": "images/slowpoke.png" },
  {"src": "images/tauros.png" },
];

const Game = () => {

  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0);
  const [choiceOne, setChoiceOne] = useState(null);
  const [choiseTwo, setChoiceTwo] = useState(null);

  //shuffle cards
  const shuffleCards = () => {
    const shuffledCards = [...cardImages, ...cardImages]
      .sort(() => Math.random() - 0.5)
        .map((card) => ({...card, id: uniqid() }));

    setCards(shuffledCards);
    setTurns(0);
  }

  //handle choice
  const handleChoice = (card) => {
    if (choiceOne) {
      setChoiceTwo(card);
    } else {
      setChoiceOne(card);
    }
  }

  //compare two selected cards
  useEffect(() => {
    if (choiceOne && choiceTwo) {
      if (choiceOne.src === choiceTwo.src) {
        console.log('those cards match');
        resetTurn();
      } else {
        console.log('those cards don\'t match');
        resetTurn();
      }
    }
  }, [choiceOne, choiceTwo])

  //reset choices & increase turns
  const resetTurn = () => {
    setChoiceOne(null);
    setChoiceTwo(null);
    setTurns(prevTurns => preTurns + 1);
  }

  return (
    <div id="game">
      <button onClick={shuffleCards}>New Game</button>
      <div className="card-grid">
        {cards.map(card =>  (
          <SingleCard
            card={card}
            func={handleChoice}
          />
        ))}
      </div>
    </div>
  );

}

export default Game;
